% Load example data (customer purchasing data)customer_data = randn(500,4); % Generate random data with 500 customers and 4 features% Display the first few rows of the datadisp('Customer purchasing behavior data generated:');disp('First 5 rows of customer data:');disp(customer_data(1:5, :)); % Display the first 5 rows for data inspection%%% Train SOM for customer segmentationnet = selforgmap([10 10]); % Define a 10x10 grid for SOM (100 neurons)disp('Initialized SOM with a 10x10 neuron grid.');net = train(net, customer_data'); % Train SOM with transposed datadisp('SOM training completed on customer purchasing data.');%%% Visualize clustersfigure;plotsompos(net, customer_data); % Visualize the SOM neurons and the data points they representtitle('SOM for Customer Segmentation');disp('SOM neuron positions visualized with customer data.');%%% Visualize the distribution of each feature for customer datafigure;for i = 1:4    subplot(2, 2, i); % Create a 2x2 grid of subplots for each feature    histogram(customer_data(:, i)); % Plot histogram for each feature    xlabel(['Feature ', num2str(i)]);    ylabel('Frequency');    title(['Distribution of Feature ', num2str(i)]);enddisp('Feature distribution histograms plotted.');%%% Visualize SOM clusters for the first two features of the customer datafigure;gscatter(customer_data(:,1), customer_data(:,2), vec2ind(net(customer_data')), 'rgbc', 'osd');xlabel('Feature 1');ylabel('Feature 2');title('SOM Clustering: Feature 1 vs Feature 2');disp('SOM clusters visualized for Feature 1 vs Feature 2.');%%